import {useForm} from 'react-hook-form';


function CheckboxRow1(hourAmount) {
    const [hour, setHour]=useState(0)
    const {register, handleSubmit} = useForm();
    function onFormSubmit(data) {
        const uren = data.uren;
      const hourNumberArray = uren.map((uur) => {
            return Number(uur);
        });
        const SumHour = hourNumberArray.reduce((a, b) => a + b,0)
        console.log(SumHour)
            setHour(SumHour);


    }


    return (
        <>


        <form onBlur={handleSubmit(onFormSubmit)}>

        <label className="container col1">
            Vormgeving
            <input type="checkbox"
                   value="5"
                   {...register("uren")}/>
            <span className="checkmark"/>


        </label>

            <label className="container col1">
                Stylen
                <input type="checkbox"
                       value="8"
                       {...register("uren")}/>
                <span className="checkmark"/>

            </label>

            <label className="container col1">
                draaien
                <input type="checkbox"
                       value="10"
                       name="hallo"
                       {...register("uren")}/>
                <span className="checkmark"/>

            </label>

</form>
        </>
)
}

export default CheckboxRow1;
