import React, { createContext, useState } from 'react';
import { useHistory } from 'react-router-dom';


export const AuthContextTest = createContext({});

function AuthContextTestProvider({ children }) {
    const [Auth, toggleAuth] = useState({
        isAuth: false,
        user: null,
    });

const history = useHistory();

    function login(){
        console.log("De user is ingelogd");
        toggleAuth({
            isAuth: true,
            user:{
                email: "Piet@novi.nl",
                username: "pieterte"
            },
        })
        history.push("/")

    }

    function logout(){
        console.log("De user is uitgelogd")
        toggleAuth({
            isAuth: false,
            user: null
        })
        history.push("/inlog-test")
    }

    const contextData = {
        isAuth: Auth.isAuth,
        login: login,
        logout: logout,
    };


    return (
        <AuthContextTest.Provider value={contextData}>
            {children}
        </AuthContextTest.Provider>
    );
}

export default AuthContextTestProvider;
