import React, {useState} from "react"
import {useForm} from 'react-hook-form';
import FormFieldText from "../Form-input/text-input/form field text";


function CheckboxRow1() {
    const [hour, setHour]=useState(0)
    const {register, handleSubmit} = useForm();
    function onFormSubmit(data) {
        const uren = data.uren;
        console.log(uren[0]);

      const hourNumberArray = uren.map((uur) => {
            return Number(uur);
        });
        const SumHour = hourNumberArray.reduce((a, b) => a + b,0)
        console.log(SumHour)
            setHour(SumHour);


    }


    return (
        <>
            <div className="counter">
                <p>Totaal aantal uur:</p>
                <div className="counter-area">
<p>{hour} uur</p>
                </div>
            </div>

        <form onBlur={handleSubmit(onFormSubmit)}>

        <label className="container col1">
            Vormgeving
            <input type="checkbox"
                   value="5"
                   {...register("uren")}/>
            <span className="checkmark"/>
            <FormFieldText/>


        </label>

            <label className="container col1">
                Stylen
                <input type="checkbox"
                       value="8"
                       {...register("uren")}/>
                <span className="checkmark"/>

            </label>

            <label className="container col1">
                draaien
                <input type="checkbox"
                       value="10"
                       name="hallo"
                       {...register("uren")}/>
                <span className="checkmark"/>

            </label>

</form>
        </>
)
}

export default CheckboxRow1;
