import React, { createContext, useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';


export const AuthContextTest = createContext({});

function AuthContextTestProvider({ children }) {
    const [isAuth, toggleIsAuth] = useState(false);
const {history} = useHistory();

    function login(){
        console.log("De user is ingelogd");
        toggleIsAuth(true)
        history.push("/")

    }

    function logout(){
        console.log("De user is uitgelogd")
        history.push("/inlog-test")
    }
    const contextData = {
        isAuth: isAuth.isAuth,
        login: login,
        logout: logout,
    };


    return (
        <AuthContextTest.Provider value={contextData}>
            {children}
        </AuthContextTest.Provider>
    );
}

export default AuthContextTestProvider;
