import React, {useEffect, useState} from "react";


function CheckboxRow1() {

    const [hour, setHour] = useState(0);
    console.log(hour)
    useEffect(() => {
        console.log("hour is veranderd!");
    }, [hour]);


    const [checked, setChecked] = useState(false);

    useEffect(() => {
        if (checked === true) {
            setHour(Number(target.value) + hour)
        } else {
            setHour(hour -1)
        }
    }, [checked]);

    return (
        <div>
            <label htmlFor="Knop"> Vormgeving
            <input
                type="checkbox"
                value="2"
                checked={setChecked}
            />

            </label>

            <label htmlFor="Knop"> Design
                <input
                    type="checkbox"
                    value="4"
                    onChange={(e) => setHour(Number(e.target.value) + hour)}
                />

            </label>

            <label>Vormgeving
                <button
                type="button"
                onClick={(e) => setHour(hour + 2)}/>
            </label>
            <p>{hour} uur totaal</p>
        </div>
    );

}

export default CheckboxRow1;
