import React, {useContext} from "react";
import {useForm} from "react-hook-form";
import axios from "axios";
import {Link} from "react-router-dom";
import AuthContext from "../../context/AuthContext--test";
import jwt_decode from "jwt-decode";


function Login() {
    const { inlog } = useContext(AuthContext);
    const {register, handleSubmit} = useForm();



    async function makeLoginRequest(data) {

        console.log(data)
        try {
            const result = await axios.post(`https://frontend-educational-backend.herokuapp.com/api/auth/signin`, {
                    // username: data.username,
                    // password: data.wachtwoord,
                username: "Rianne",
                password:"loploplop"
                }
            );
            console.log(result.data.accessToken)
            const decodedToken = jwt_decode(result.data.accessToken);
            console.log(decodedToken.sub)
            inlog(result.data.accessToken);
        } catch (e) {
            console.error(e)
        }
    }

    return (
        <>
            <h1>Inloggen</h1>
            <p>Vul de velden hieronder in om in te kunnen loggen.</p>

            <form onSubmit={handleSubmit(makeLoginRequest)}>
                {/*<label htmlFor="form-email">*/}
                {/*    Gebruikersnaam:*/}
                {/*    <input*/}
                {/*        type="text"*/}
                {/*        id="form-username"*/}
                {/*        {...register("username")}*/}
                {/*        placeholder="gebruikersnaam"*/}
                {/*    />*/}
                {/*</label>*/}
                {/*<label htmlFor="form-wachtwoord">*/}
                {/*    Wachtwoord:*/}
                {/*    <input*/}
                {/*        type="password"*/}
                {/*        id="form-wachtwoord"*/}
                {/*        {...register("wachtwoord")}*/}
                {/*        placeholder="wachtwoord"*/}
                {/*    />*/}
                {/*</label>*/}
                <button
                    type="submit"
                >Inloggen
                </button>
            </form>
            <p>Geen account? Dat kan ook. Wil je er toch een? Klik dan <Link to="/registratie">hier!</Link></p>
        </>
    )
}

export default Login
