import React, {useContext} from "react";
import {Link} from "react-router-dom";
import  {AuthContext} from "../../context/AuthContext";


function InlogPage() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, toggleError] = useState(false);
    const { login } = useContext(AuthContext);

    async function handleSubmit(e) {
        e.preventDefault();
        toggleError(false);

        try {
            const result = await axios.post('http://localhost:3000/login', {
                email: email,
                password: password,
            });
            // log het resultaat in de console
            console.log(result.data);

            // geef de JWT token aan de login-functie van de context mee
            login(result.data.accessToken);

        } catch(e) {
            console.error(e);
            toggleError(true);
        }
    }


    return (
        <>
            <form onSubmit={handleSubmit(onFormSubmit)}>

                <label htmlFor="Email">
                    Email:
                    <input
                        type="email"
                        id="Email"
                        {...register("Email", {
                            required: {
                                value: true,
                                message: "Dit veld is verplicht"
                            },

                        })}/>
                    {errors.Email && <p>{errors.Email.message}</p>}
                </label>

                <label htmlFor="password">
                    Wachtwoord:
                    <input
                        type="password"
                        id="password"
                        {...register("password",
                            {
                                minLength:
                                    {
                                        value: 8,
                                        message: "Je wachtwoord is min. 8 tekens lang",
                                    },
                            })}/>
                    {errors.password && <div className="Message--error"><p>{errors.password.message}</p></div>}
                </label>


                <button
                    type="submit"
                >
                    versturen
                </button>


            </form>

            <p>Heb je nog geen account? <Link to="/registreer">Registreer</Link> je dan eerst.</p>
        </>


    )
}


export default InlogPage
