import React, {useEffect, useState} from "react";
import {useForm} from "react-hook-form";


function CheckboxRow1({title, hourAmount, name}) {
    const {register} = useForm();
    const [hour, setHour] = useState(0);

        useEffect(() => {
            console.log("hour is veranderd!");
        }, [hour]);

        return (
            <input type="checkbox"
                   id={title}
                   onChange={() => setHour(hour + {hourAmount})}
                   name={title}
                   {...register(name)}
            />

        );

}
export default CheckboxRow1;
